import { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom"; // Import Routes for routing project-specific pages
import Contact from "./views/Contact";
import Navbar from "./components/Navbar";
import About from "./views/About";
import Home from './views/Home';
import Services from "./views/Services";
import Projects from "./views/Projects";
import Project1 from './views/Project1';  // Import Project1 view
import LoadingScreen from "./components/LoadingScreen";
import { ThemeProvider } from "./themeProvider";

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => setLoading(false), 1000);
  }, []);

  return (
    <ThemeProvider>
      <>
        {!loading ? (
          <div>
            <Router>
              <Navbar />
              {/* Render all static views directly */}
              <Home />
              <About />
              <Services />
              <Projects />
              <Contact />

              {/* Only Route project-specific pages */}
              <Routes>
                <Route path="/project1" element={<Project1 />} />
              </Routes>
            </Router>
          </div>
        ) : (
          <LoadingScreen />
        )}
      </>
    </ThemeProvider>
  );
}

export default App;
